const e=(e,t="YYYY-MM-DD HH:mm:ss")=>{if(!e)return"-";let r;try{if("string"==typeof e)if(/^\d+$/.test(e))r=new Date(parseInt(e));else{if(!(e.includes("T")||e.includes("-")||e.includes("/")))return e;r=new Date(e)}else r=new Date(e);if(isNaN(r.getTime()))return String(e);const n=r.getFullYear(),a=String(r.getMonth()+1).padStart(2,"0"),i=String(r.getDate()).padStart(2,"0"),s=String(r.getHours()).padStart(2,"0"),g=String(r.getMinutes()).padStart(2,"0"),c=String(r.getSeconds()).padStart(2,"0");return t.replace("YYYY",String(n)).replace("MM",a).replace("DD",i).replace("HH",s).replace("mm",g).replace("ss",c)}catch(n){return console.error("时间格式化错误:",n),String(e)}};export{e as f};
